public class Pattern {
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=5;//rows
		int j=5;
		for( i=1;i<=5;i++) {//1,2
			
		for( j=1;j<=i;j++)
		{
			System.out.print("*");//*, ,***,
		}
		System.out.println();
			
		}
		
	}

	}


